    @Override
    public List<FlightInfo> process(String html, FlightSearch param) {
        List<FlightInfo> flightinfos = new ArrayList<FlightInfo>();
        JSONObject jsonobject = JSONObject.fromObject(html);
        JSONObject json_tripItem = (JSONObject) jsonobject.getJSONArray("tripItemList").get(0);

        JSONArray jsonarray_airRoutingList = json_tripItem.getJSONArray("airRoutingList");
        for (int i = 0; i < jsonarray_airRoutingList.size(); i++) {
            FlightInfo flightInfo = new FlightInfo();
            Float basePrice = 0f;

            JSONObject flight_JSONObject = (JSONObject) jsonarray_airRoutingList.get(i);
            JSONArray flightList_JSONArray = flight_JSONObject.getJSONArray("flightList");
            if (flightList_JSONArray.size() == 1) {
                JSONObject flight = (JSONObject) flightList_JSONArray.get(0);
                //                if ("MU".equals(flight.getString("carrier"))) {
                flightInfo.setAirline(flight.getString("flightNo"));
                flightInfo.setStartAirport(flight.getString("deptCd"));// 起飞机场
                flightInfo.setStartAirportName(flight.getString("deptCityNm"));// 起飞起场名称
                flightInfo.setEndAirport(flight.getString("arrCd"));// 到达机场
                flightInfo.setEndAirportName(flight.getString("flightNo"));// 到达机场名称
                flightInfo.setStartAirportCity(flight.getString("arrCityNm"));// 起飞起场城市名称
                flightInfo.setAirline(flight.getString("flightNo"));// 航线
                flightInfo.setAirCompany(flight.getString("carrier"));// 航空公司
                flightInfo.setAirCompanyName(flight.getString("airlineName"));// 航空公司名称airlineName
                //                    flightInfo.setAirportFee(flight.getString("carrier"));// 机场建设费
                //                    flightInfo.setFuelFee(flight.getString("carrier"));// 燃油费
                //                    flightInfo.setDistance(flight.getString("carrier"));// 里程数
                //                    flightInfo.setMeal(flight.getString("carrier"));
                SimpleDateFormat dateFormat = new SimpleDateFormat("yyyy-MM-dd HHmm");
                Date startDate;
                Date arriveDate;
                try {
                    startDate = dateFormat.parse(flight.getString("deptTime"));
                    arriveDate = dateFormat.parse(flight.getString("arrTime"));
                    //                    if (Integer.valueOf(arriTime) < Integer.valueOf(depTime)) {
                    //                        arriveDate = new Date(arriveDate.getTime() + 24 * 3600 * 1000);
                    //                    }
                    flightInfo.setDepartTime(new Timestamp(startDate.getTime()));// 起飞时间
                    flightInfo.setArriveTime(new Timestamp(arriveDate.getTime()));// 到达时间
                }
                catch (Exception e) {
                }

                flightInfo.setIsShare(0);
                flightInfo.setAirplaneType(flight.getString("carrier"));// 飞机型号
                flightInfo.setAirplaneTypeDesc("");// 飞机型号描述
                flightInfo.setOffPointAT(flight.getString("deptTerm"));// 出发航站楼
                flightInfo.setBorderPointAT(flight.getString("arrTerm"));// 到达航站楼
                //                    if (stopnum > 0) {
                //                        flightInfo.setStop(true);
                //                        flightInfo.setIsStopInfo(stopnum + "");
                //                    }
                //                    else {
                flightInfo.setStop(false);
                //                    }
                //                }
                //                else {
                //                    continue;
                //                }
            }
            else {
                continue;
            }
            JSONArray productInfoList = flight_JSONObject.getJSONArray("productInfoList");
            List<CarbinInfo> listCabinAll = new ArrayList<CarbinInfo>();
            if (productInfoList.size() > 0) {
                for (int j = 0; j < productInfoList.size(); j++) {
                    JSONObject productInfo = (JSONObject) productInfoList.get(j);
                    CarbinInfo cabin = new CarbinInfo();
                    cabin.setCabin(productInfo.getString("cabinCode"));
                    cabin.setCabintypename(productInfo.getString("productNm"));
                    //                    if (seatStatus != null && seatStatus.equals("A")) {
                    cabin.setSeatNum("9");
                    //                    }
                    //                    else {
                    //                        cabin.setSeatNum(seatStatus);
                    //                    }
                    //                    if (discount >= 0) {

                    cabin.setDiscount(10F);
                    //                    }

                    //                    if (wsSeatWithPriceAndComisionItem.getSeatType() == 3) {
                    //                    cabin.setSpecial(true);
                    //                    }
                    cabin.setPrice(Float.parseFloat(productInfo.getString("priceAmountAdt")));
                    if ("Y".equals(productInfo.getString("cabinCode"))) {
                        basePrice = Float.parseFloat(productInfo.getString("priceAmountAdt"));
                    }
                    listCabinAll.add(cabin);
                }
            }
            else {
                continue;
            }
            flightInfo.setCarbins(listCabinAll);// 加入 仓位信息
            flightInfo.setYPrice(basePrice);// 全价价格
            flightinfos.add(flightInfo);
        }
        return flightinfos;
    }

